	font-weight: bold;
}
td.btl-grid-data {
	padding: 1px 5px;
}
/* header */
.btl-grid-header-table,
.btl-grid-scrollers { /* for space above/below scrollbar on the right */
	background-color: ThreeDFace;
	color: ButtonText;
}
.btl-grid-resize-shadow {
	background-color: ThreeDFace;
