d:element.d:template:
// Scan for <b:xml>
var aX = bb.xml.getElementsByTagNameNS(this.modelNode, btl.namespaceURI, 'xml');
var aS = [];
for (var i = aX.length-1; i >= 0 ; i--) {
	aS.push(aX[i]);
	var oM = this.modelNode.ownerDocument.createElement('marker');
	aX[i].parentNode.replaceChild(oM, aX[i]);
}
aX = aS;

// Render plain HTML
var sRender = '<div>'+bb.xml.serialize(this.modelNode, true)+'</div>';
var oElm = bb.html.createElementFromString(sRender);

// Replace <b:xml> back into tree.
var aM = oElm.getElementsByTagName('marker');
for (var i = aX.length-1; i >= 0 ; i--) {
	bb.construct(aX[i]);
	var oC = bb.getControllerFromModel(aX[i]);
	var oTElm = oC.viewNode;
	oC.viewNode = null;
	oC.viewGate = null;
	aM[i].parentNode.replaceChild(oTElm, aM[i]);
	this.appendChild(oC);
	oC.viewNode = oTElm;
	oC.viewGate = oTElm;
}
return [oElm, oElm];
